const openMenuHandlers = {
	handleInputChange() {
	  turn it on
  },
	handlePressArrowDown(e) {
		if(off and down arrow key pressed) {
			turn it on
    }
  }

	...

}

const closeMenuHandlers = {
	handlePressArrowup(e) {
		if(on and no selected item) {
			turn it off
    }
  }

	...

}

const handleKeyDown = (e) => {
 const listeners = [
	 openMenuHandlers.handlePressArrowDown,
	 closeMenuHandlers.handlePressArrowUp,
	 ...
 ]

	listenres.forEach(callback => callback(e))

}
...

<input 
	on:keydown={handleKeyDown}
	...
/>

